#include <stdlib.h>
#include <stdio.h>

int main(){
    int a[200];
    char b[200];
    int i,m,n,t,x = 0,j,temp;
    scanf("%d",&t);
    for(i=0;i<t;i++)
    {
        n=0;
        scanf("%c",&b[0]);
        while(b[n]!='0')
        {
            n++;
        scanf("%c",&b[n]);
        }

        n=0;
        while(b[n]!='0')
        {
            if(b[n]>='1'){
                a[x]=b[n]-'0';
                x++;
            }
            n++;
        }
    }

    for(j=0;j<x-1;j++)
    {
        for(m=0;m<x-j-1;m++)
        {
            if(a[m]>a[m+1])
            {
                temp=a[m];
                a[m]=a[m+1];
                a[m+1]=temp;
            }
        }
    }
    for(m=0;m<x;m++)
    printf("%d,",a[m]);
    printf("\n");

    return 0;
}

